const getConfig = async () => {
  const config = { cardConfig: { data: { billingAddress: {} } } };
  config.environment = await httpGet('env', 'ENVIRONMENT');
  config.clientKey = await httpGet('env', 'CHECKOUT_CLIENTKEY');
  return config;
};

let card;

const loadComponent = function loadComponent() {
  defaultLocaleConfig().then(() => {
    const localeConfig = collectLocaleConfig();
    getConfig().then((config) => {
      (async function(){

        const translations = {
          "en-US": {
            "creditCard.numberField.placeholder": "Card number",
            "creditCard.expiryDateField.placeholder": "Expiry date"
          }
        };

        const { AdyenCheckout, Card } = window.AdyenWeb;
        const checkout = await AdyenCheckout({
          environment: config.environment,
          clientKey: config.clientKey,
          locale: localeConfig.locale,
          countryCode: localeConfig.countryCode,
          translations: translations,
        });

        var styleObject = {
          base: {
            fontSize: '20px',
            fontFamily: 'Comic Sans MS'
          }
        };

        card = new Card(checkout, {
            styles: styleObject,
            ...config.cardConfig,
            onFocus: (state) => {
              document.querySelectorAll('.adyen-checkout__field--error').forEach(function(item) {
                item.querySelector('.adyen-checkout__icon').src = 'data:image/png;base64,';
                })
            },
          })
          .mount('#card-container');
      })()
    });
  });
};

loadComponent();
